        <script>
            $('.menu__item').click(function(e){
                e.preventDefault();
                var target = $($(this).attr('href'));
                if(target.length){
                    var scrollTo = target.offset().top;
                    scrollTo -= 150;
                    $('body, html').animate({scrollTop: scrollTo+'px'}, 800);
                }
            });
        </script>
